mapper.select:
select * from orders
where
user_id=#{userId}
and status=#{status}
order by order_time desc

select * from orders


     order_time >= #{beginTime}
    and order_time <= #{endTime}
    and number like CONCAT('%',#{number},'%')
    and phone like CONCAT('%',#{phone},'%')
    and status=#{status}

order by order_time desc

select count(id) from orders

    order_time >= #{start}
    and order_time <= #{end}
    and status=#{status}

select name,sum(order_detail.number) as number FROM orders LEFT JOIN order_detail on orders.id=order_detail.order_id

    status=#{status}
    and order_time >= #{start}
    and order_time <= #{end}

group by name order by number desc limit 0,10
